// App.jsx CORRIGIDO - Sem erro do SignatureCanvas
import { useState, useRef } from 'react';
import SignatureCanvas from 'react-signature-canvas';
import './App.css';

function App() {
  const [cpf, setCpf] = useState('');
  const [inscrito, setInscrito] = useState(null);
  const [pdfUrl, setPdfUrl] = useState('');
  const [assinado, setAssinado] = useState(false);
  const [carregando, setCarregando] = useState(false);
  const sigCanvas = useRef();

  const buscarInscrito = async () => {
    setCarregando(true);
    setInscrito(null);
    setPdfUrl('');
    setAssinado(false);

    try {
      const res = await fetch(`/api/inscrito/${cpf}`);
      if (res.ok) {
        const data = await res.json();
        setInscrito(data);

        // PDF é gerado automaticamente na pesquisa
        if (data.pdf_url) {
          setPdfUrl(data.pdf_url);
        }

        // Verificar se já foi assinado
        if (data.assinatura_realizada) {
          setAssinado(true);
        }
      } else {
        alert('Inscrito não encontrado');
      }
    } catch (error) {
      console.error('Erro ao buscar inscrito:', error);
      alert('Erro ao buscar inscrito. Tente novamente.');
    } finally {
      setCarregando(false);
    }
  };

  const adicionarAssinatura = async () => {
    // Verificação corrigida para o SignatureCanvas
    if (!sigCanvas.current) {
      alert("Erro: Canvas de assinatura não encontrado.");
      return;
    }

    // Verificar se há assinatura de forma mais robusta
    try {
      const canvas = sigCanvas.current.getCanvas();
      if (!canvas) {
        alert("Erro: Canvas não disponível.");
        return;
      }

      // Verificar se o canvas está vazio
      const context = canvas.getContext('2d');
      const imageData = context.getImageData(0, 0, canvas.width, canvas.height);
      const isEmpty = !imageData.data.some(channel => channel !== 0);

      if (isEmpty) {
        alert("Por favor, assine o termo antes de continuar.");
        return;
      }

      setCarregando(true);

      // Obter assinatura de forma mais segura
      let assinatura;
      try {
        // Tentar usar getTrimmedCanvas primeiro
        if (sigCanvas.current.getTrimmedCanvas) {
          assinatura = sigCanvas.current.getTrimmedCanvas().toDataURL('image/png');
        } else {
          // Fallback para getCanvas se getTrimmedCanvas não estiver disponível
          assinatura = sigCanvas.current.getCanvas().toDataURL('image/png');
        }
      } catch (trimError) {
        console.warn('Erro ao usar getTrimmedCanvas, usando getCanvas:', trimError);
        assinatura = sigCanvas.current.getCanvas().toDataURL('image/png');
      }

      const payload = {
        cpf,
        assinatura,
      };

      const res = await fetch('/api/atualizar-assinatura', {
        method: 'POST',
        body: JSON.stringify(payload),
        headers: { 'Content-Type': 'application/json' },
      });

      if (res.ok) {
        const result = await res.json();
        setPdfUrl(result.pdf);
        setAssinado(true);
        alert(result.mensagem || 'Assinatura adicionada com sucesso!');

        // Limpar assinatura após sucesso
        if (sigCanvas.current && sigCanvas.current.clear) {
          sigCanvas.current.clear();
        }
      } else {
        const error = await res.json();
        alert(error.error || 'Erro ao adicionar assinatura');
      }
    } catch (error) {
      console.error('Erro ao adicionar assinatura:', error);
      alert('Erro ao adicionar assinatura. Tente novamente.');
    } finally {
      setCarregando(false);
    }
  };

  const limparAssinatura = () => {
    if (sigCanvas.current && sigCanvas.current.clear) {
      sigCanvas.current.clear();
    }
  };

  const limparFormulario = () => {
    setCpf('');
    setInscrito(null);
    setPdfUrl('');
    setAssinado(false);
    limparAssinatura();
  };

  return (
    <div className="App">
      <div className="container">
        <header className="header">
          <h1>Sistema de Termo de Responsabilidade</h1>
          <p>ACAMP RELEVANTE JUNIORS 2025</p>
        </header>

        <div className="busca-section">
          <div className="input-group">
            <input 
              type="text"
              value={cpf} 
              onChange={(e) => setCpf(e.target.value)} 
              placeholder="Digite o CPF do Junior"
              disabled={carregando}
              className="cpf-input"
            />
            <button 
              onClick={buscarInscrito} 
              disabled={carregando || !cpf.trim()}
              className="btn-primary"
            >
              {carregando ? 'Buscando...' : 'Buscar'}
            </button>
          </div>

          {inscrito && (
            <button onClick={limparFormulario} className="btn-secondary">
              Nova Busca
            </button>
          )}
        </div>

        {inscrito && (
          <div className="inscrito-info">
            <h2>Dados do Inscrito</h2>
            <div className="info-grid">
              <div className="info-item">
                <strong>Responsável:</strong> {inscrito.responsavel}
              </div>
              <div className="info-item">
                <strong>Filho(a):</strong> {inscrito.nome_completo}
              </div>
              <div className="info-item">
                <strong>Líder:</strong> {inscrito.lider_celula || 'NÃO INFORMADO'}
              </div>
              <div className="info-item">
                <strong>Telefone:</strong> {inscrito.tel_responsavel || 'NÃO INFORMADO'}
              </div>
            </div>

            {pdfUrl && (
              <div className="pdf-section">
                <h3>Termo de Responsabilidade</h3>
                <div className="pdf-actions">
                  <a 
                    href={pdfUrl} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="btn-link"
                  >
                    📄 Visualizar Termo (PDF)
                  </a>
                  <a 
                    href={pdfUrl} 
                    download={`termo_${cpf}.pdf`} 
                    className="btn-link"
                  >
                    💾 Baixar PDF
                  </a>
                </div>

                {assinado ? (
                  <div className="status-assinado">
                    <div className="success-icon">✅</div>
                    <h4>Termo já assinado!</h4>
                    <p>O documento está completo e pode ser visualizado no link acima.</p>
                  </div>
                ) : (
                  <div className="assinatura-section">
                    <h3>Adicionar Assinatura</h3>
                    <p>O termo foi gerado com os dados do inscrito. Agora adicione sua assinatura:</p>

                    <div className="signature-container">
                      <div className="signature-label">Assine no espaço abaixo:</div>
                      <SignatureCanvas 
                        ref={sigCanvas} 
                        canvasProps={{ 
                          width: 500, 
                          height: 200, 
                          className: 'sigCanvas' 
                        }}
                        backgroundColor="rgba(255,255,255,1)"
                        penColor="black"
                      />
                    </div>

                    <div className="signature-actions">
                      <button 
                        onClick={adicionarAssinatura} 
                        disabled={carregando}
                        className="btn-primary"
                      >
                        {carregando ? 'Processando...' : '✍️ Adicionar Assinatura'}
                      </button>
                      <button 
                        onClick={limparAssinatura}
                        disabled={carregando}
                        className="btn-secondary"
                      >
                        🗑️ Limpar Assinatura
                      </button>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
