// App.jsx
import { useState, useRef } from 'react';
import SignatureCanvas from 'react-signature-canvas';
import './App.css';


function App() {
  const [cpf, setCpf] = useState('');
  const [inscrito, setInscrito] = useState(null);
  const [pdfUrl, setPdfUrl] = useState('');
  const sigCanvas = useRef();

  const buscarInscrito = async () => {
    const res = await fetch(`/api/inscrito/${cpf}`);
    if (res.ok) {
      const data = await res.json();
      setInscrito(data);
    } else {
      alert('Inscrito não encontrado');
    }
  };

  const assinar = async () => {
    if (!sigCanvas.current || sigCanvas.current.isEmpty()) {
      alert("Por favor, assine o termo antes de continuar.");
      return;
    }

    const assinatura = sigCanvas.current.getTrimmedCanvas().toDataURL('image/png');

    const payload = {
      cpf,
      nome: inscrito.nome_completo,
      nome_filho: inscrito.responsavel,
      contato_nome: inscrito.lider_celula || 'NÃO INFORMADO',
      contato_telefone: inscrito.tel_responsavel || 'NÃO INFORMADO',
      assinatura,
    };

    const res = await fetch('/api/assinar', {
      method: 'POST',
      body: JSON.stringify(payload),
      headers: { 'Content-Type': 'application/json' },
    });

    if (res.ok) {
      const result = await res.json();
      setPdfUrl(result.pdf);
      alert('Assinado com sucesso!');
    } else {
      alert('Erro ao assinar termo');
    }
  };

  return (
    <div className="App">
      <h1>Assinar Termo de Responsabilidade</h1>
      <input value={cpf} onChange={(e) => setCpf(e.target.value)} placeholder="Digite o CPF" />
      <button onClick={buscarInscrito}>Buscar</button>

      {inscrito && (
        <div>
          <h2>Confirmação</h2>
          <p><strong>Responsável:</strong> {inscrito.nome_completo}</p>
          <p><strong>Filho(a):</strong> {inscrito.responsavel}</p>
          <p><strong>Contato:</strong> {inscrito.tel_responsavel}</p>

          <p>Assine abaixo para confirmar:</p>
          <SignatureCanvas ref={sigCanvas} canvasProps={{ width: 400, height: 150, className: 'sigCanvas' }} />
          <button onClick={assinar}>Assinar</button> 
          <button onClick={() => sigCanvas.current.clear()}>Limpar Assinatura</button>

        </div>
      )}

      {pdfUrl && (
        <div style={{ marginTop: 20 }}>
          <p>Termo assinado disponível em:</p>
          <a href={pdfUrl} target="_blank" rel="noopener noreferrer">Visualizar PDF</a>
        </div>
      )}
    </div>
  );
}

export default App;
