// App.jsx CORRIGIDO - URLs corretas para visualiza√ß√£o de PDF
import { useState, useRef } from 'react';
import SignatureCanvas from 'react-signature-canvas';
import './App.css';

function App() {
  const [cpf, setCpf] = useState('');
  const [inscrito, setInscrito] = useState(null);
  const [pdfUrl, setPdfUrl] = useState('');
  const [assinado, setAssinado] = useState(false);
  const [carregando, setCarregando] = useState(false);
  const sigCanvas = useRef();

  // Fun√ß√£o para formatar CPF
  const formatarCpf = (valor) => {
    const numeros = valor.replace(/\D/g, '');
    return numeros.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
  };

  // Fun√ß√£o para validar CPF b√°sico
  const validarCpf = (cpf) => {
    const numeros = cpf.replace(/\D/g, '');
    return numeros.length === 11;
  };

  const buscarInscrito = async () => {
    if (!validarCpf(cpf)) {
      alert('Por favor, digite um CPF v√°lido com 11 d√≠gitos.');
      return;
    }

    setCarregando(true);
    setInscrito(null);
    setPdfUrl('');
    setAssinado(false);

    try {
      const cpfNumeros = cpf.replace(/\D/g, '');
      console.log('üîç Buscando CPF:', cpfNumeros);

      const res = await fetch(`/api/inscrito/${cpfNumeros}`);

      if (res.ok) {
        const data = await res.json();
        console.log('‚úÖ Dados recebidos:', data);

        setInscrito(data);

        // CORRIGIDO: URL ser√° /assinados/CPF.pdf (servida pelo servidor na porta 3001)
        if (data.pdf_url) {
          const pdfUrlCompleta = `http://localhost:3001${data.pdf_url}`;
          setPdfUrl(pdfUrlCompleta);
          console.log('üìÑ URL do PDF:', pdfUrlCompleta);
        }

        // Verificar se j√° foi assinado
        if (data.assinatura_realizada) {
          setAssinado(true);
        }
      } else {
        const errorData = await res.json();
        alert(errorData.error || 'Inscrito n√£o encontrado');
      }
    } catch (error) {
      console.error('‚ùå Erro ao buscar inscrito:', error);
      alert('Erro ao buscar inscrito. Verifique se o servidor est√° rodando.');
    } finally {
      setCarregando(false);
    }
  };

  const adicionarAssinatura = async () => {
    // Verifica√ß√£o corrigida para o SignatureCanvas
    if (!sigCanvas.current) {
      alert("Erro: Canvas de assinatura n√£o encontrado.");
      return;
    }

    // Verificar se h√° assinatura
    if (sigCanvas.current.isEmpty()) {
      alert("Por favor, fa√ßa sua assinatura antes de continuar.");
      return;
    }

    try {
      console.log('‚úçÔ∏è Processando assinatura...');

      // Obter dados da assinatura
      const assinaturaDataURL = sigCanvas.current.toDataURL();
      const cpfNumeros = cpf.replace(/\D/g, '');

      console.log('üìù Enviando assinatura para CPF:', cpfNumeros);

      const response = await fetch('/api/atualizar-assinatura', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          cpf: cpfNumeros,
          assinatura: assinaturaDataURL
        })
      });

      if (response.ok) {
        const result = await response.json();
        console.log('‚úÖ Assinatura adicionada:', result);

        setAssinado(true);

        // CORRIGIDO: Atualizar URL do PDF com vers√£o assinada
        const pdfUrlAtualizada = `http://localhost:3001${result.pdf}?t=${Date.now()}`;
        setPdfUrl(pdfUrlAtualizada);

        alert('Assinatura adicionada com sucesso!');

        // Limpar canvas
        sigCanvas.current.clear();
      } else {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Erro ao adicionar assinatura');
      }
    } catch (error) {
      console.error('‚ùå Erro ao adicionar assinatura:', error);
      alert('Erro ao adicionar assinatura: ' + error.message);
    }
  };

  const limparAssinatura = () => {
    if (sigCanvas.current) {
      sigCanvas.current.clear();
    }
  };

  const handleCpfChange = (e) => {
    const valor = e.target.value;
    const valorFormatado = formatarCpf(valor);
    if (valorFormatado.length <= 14) { // Limite do formato XXX.XXX.XXX-XX
      setCpf(valorFormatado);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      buscarInscrito();
    }
  };

  return (
    <div className="App">
      <div className="container">
        <header className="header">
          <h1>Sistema de Termo de Responsabilidade</h1>
          <p>ACAMP RELEVANTE JUNIORS 2025</p>
        </header>

        <section className="busca-section">
          <div className="input-group">
            <input
              type="text"
              className="cpf-input"
              placeholder="Digite o CPF (apenas n√∫meros)"
              value={cpf}
              onChange={handleCpfChange}
              onKeyPress={handleKeyPress}
              disabled={carregando}
            />
            <button 
              className="btn-primary" 
              onClick={buscarInscrito}
              disabled={carregando || !cpf}
            >
              {carregando ? 'Buscando...' : 'Buscar Inscrito'}
            </button>
          </div>
        </section>

        {inscrito && (
          <section className="inscrito-info">
            <h2>Dados do Inscrito</h2>
            <div className="info-grid">
              <div className="info-item">
                <strong>Nome:</strong> {inscrito.nome_completo}
              </div>
              <div className="info-item">
                <strong>CPF:</strong> {formatarCpf(inscrito.documento)}
              </div>
              <div className="info-item">
                <strong>Email:</strong> {inscrito.email}
              </div>
              <div className="info-item">
                <strong>Celular:</strong> {inscrito.celular}
              </div>
              <div className="info-item">
                <strong>Campus:</strong> {inscrito.campus}
              </div>
              <div className="info-item">
                <strong>Respons√°vel:</strong> {inscrito.responsavel}
              </div>
              <div className="info-item">
                <strong>Tel. Respons√°vel:</strong> {inscrito.tel_responsavel}
              </div>
              <div className="info-item">
                <strong>L√≠der de C√©lula:</strong> {inscrito.lider_celula}
              </div>
            </div>

            {pdfUrl && (
              <div className="pdf-section">
                <h3>Termo de Responsabilidade</h3>
                <div className="pdf-actions">
                  <a 
                    href={pdfUrl} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="btn-link"
                  >
                    üìÑ Visualizar PDF
                  </a>
                  <a 
                    href={pdfUrl} 
                    download={`termo_${inscrito.documento}.pdf`}
                    className="btn-link"
                  >
                    üíæ Baixar PDF
                  </a>
                </div>

                {assinado ? (
                  <div className="status-assinado">
                    <div className="success-icon">‚úÖ</div>
                    <h4>Termo Assinado com Sucesso!</h4>
                    <p>O termo foi assinado digitalmente.</p>
                    <p>Voc√™ pode visualizar ou baixar o PDF assinado acima.</p>
                  </div>
                ) : (
