  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [modoEdicao, setModoEdicao] = useState(false);
  const [dadosEditados, setDadosEditados] = useState({});
  const [salvandoEdicao, setSalvandoEdicao] = useState(false);
  const sigCanvas = useRef({});

  // Função para buscar inscrito
  const buscarInscrito = async () => {
    if (!cpf.trim()) {
      setError('Por favor, digite um CPF');
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');
    setInscrito(null);
    setModoEdicao(false);

    try {
      const response = await fetch(`http://localhost:3001/api/inscrito/${cpf}`);
      const data = await response.json();

      if (response.ok) {
        setInscrito(data);
        setDadosEditados(data); // Inicializar dados editados
        setSuccess('Inscrito encontrado e PDF gerado!');
      } else {
        setError(data.error || 'Erro ao buscar inscrito');
      }
    } catch (err) {
      setError('Erro de conexão com o servidor');
      console.error('Erro:', err);
    } finally {
      setLoading(false);
    }
  };

  // Função para salvar dados editados
  const salvarDadosEditados = async () => {
    setSalvandoEdicao(true);
    setError('');
    setSuccess('');

    try {
      const response = await fetch(`http://localhost:3001/api/inscrito/${cpf}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(dadosEditados),
      });

      const data = await response.json();

      if (response.ok) {
        setInscrito(data);
        setModoEdicao(false);
        setSuccess('Dados atualizados e PDF regenerado com sucesso!');
      } else {
        setError(data.error || 'Erro ao atualizar dados');
      }
    } catch (err) {
      setError('Erro de conexão com o servidor');
      console.error('Erro:', err);
    } finally {
      setSalvandoEdicao(false);
    }
  };

  // Função para cancelar edição
  const cancelarEdicao = () => {
    setDadosEditados(inscrito); // Restaurar dados originais
    setModoEdicao(false);
  };

  // Função para atualizar campo editado
  const atualizarCampo = (campo, valor) => {
    setDadosEditados(prev => ({
      ...prev,
      [campo]: valor
    }));
  };

  // Função para adicionar assinatura
  const adicionarAssinatura = async () => {
    if (!inscrito) {
      setError('Nenhum inscrito selecionado');
      return;
    }

    if (!sigCanvas.current || sigCanvas.current.isEmpty()) {
      setError('Por favor, faça sua assinatura antes de continuar');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const assinaturaDataURL = sigCanvas.current.toDataURL();

      const response = await fetch('http://localhost:3001/api/atualizar-assinatura', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          cpf: inscrito.documento,
          assinatura: assinaturaDataURL,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setSuccess('Assinatura adicionada com sucesso!');
        setInscrito(prev => ({ ...prev, assinatura_realizada: true }));
        sigCanvas.current.clear();
      } else {
        setError(data.error || 'Erro ao adicionar assinatura');
      }
    } catch (err) {
      setError('Erro de conexão com o servidor');
      console.error('Erro:', err);
    } finally {
      setLoading(false);
    }
  };

  // Função para limpar assinatura
  const limparAssinatura = () => {
    if (sigCanvas.current) {
      sigCanvas.current.clear();
    }
  };

  return (
    <div className="App">
      <div className="container">
        <header className="header">
          <h1>Sistema de Termo de Responsabilidade</h1>
          <p>ACAMP RELEVANTE JUNIORS 2025</p>
        </header>

        {/* Seção de busca */}
        <div className="search-section">
          <div className="input-group">
            <input
              type="text"
              value={cpf}
              onChange={(e) => setCpf(e.target.value)}
              placeholder="Digite o CPF do inscrito"
              className="cpf-input"
              disabled={loading}
            />
            <button 
              onClick={buscarInscrito} 
              disabled={loading}
              className="search-button"
            >
              {loading ? 'Buscando...' : 'Buscar Inscrito'}
            </button>
          </div>
        </div>

        {/* Mensagens */}
        {error && <div className="message error">{error}</div>}
        {success && <div className="message success">{success}</div>}

        {/* Dados do inscrito */}
        {inscrito && (
          <div className="inscrito-section">
            <div className="inscrito-header">
              <h2>Dados do Inscrito</h2>
              {!modoEdicao && (
                <button 
                  onClick={() => setModoEdicao(true)}
                  className="edit-button"
                >
                  ✏️ Editar Dados
                </button>
              )}
            </div>

            {modoEdicao ? (
              // Modo de edição
              <div className="edit-form">
                <div className="form-grid">
                  <div className="form-group">
                    <label>Nome Completo:</label>
                    <input
                      type="text"
                      value={dadosEditados.nome_completo || ''}
                      onChange={(e) => atualizarCampo('nome_completo', e.target.value)}
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label>Responsável:</label>
                    <input
                      type="text"
                      value={dadosEditados.responsavel || ''}
                      onChange={(e) => atualizarCampo('responsavel', e.target.value)}
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label>Líder de Célula:</label>
                    <input
                      type="text"
                      value={dadosEditados.lider_celula || ''}
                      onChange={(e) => atualizarCampo('lider_celula', e.target.value)}
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label>Telefone do Responsável:</label>
                    <input
                      type="text"
                      value={dadosEditados.tel_responsavel || ''}
                      onChange={(e) => atualizarCampo('tel_responsavel', e.target.value)}
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label>Email:</label>
                    <input
                      type="email"
                      value={dadosEditados.email || ''}
                      onChange={(e) => atualizarCampo('email', e.target.value)}
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label>Celular:</label>
                    <input
                      type="text"
                      value={dadosEditados.celular || ''}
                      onChange={(e) => atualizarCampo('celular', e.target.value)}
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label>Data de Nascimento:</label>
                    <input
                      type="date"
                      value={dadosEditados.data_nascimento || ''}
                      onChange={(e) => atualizarCampo('data_nascimento', e.target.value)}
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label>Campus:</label>
                    <input
                      type="text"
                      value={dadosEditados.campus || ''}
                      onChange={(e) => atualizarCampo('campus', e.target.value)}
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label>Idade:</label>
                    <input
                      type="number"
                      value={dadosEditados.idade || ''}
                      onChange={(e) => atualizarCampo('idade', e.target.value)}
                      className="form-input"
                    />
                  </div>
                </div>

                <div className="edit-buttons">
                  <button 
                    onClick={salvarDadosEditados}
                    disabled={salvandoEdicao}
                    className="save-button"
                  >
                    {salvandoEdicao ? 'Salvando...' : '💾 Salvar e Regenerar PDF'}
                  </button>
                  <button 
                    onClick={cancelarEdicao}
                    disabled={salvandoEdicao}
                    className="cancel-button"
                  >
                    ❌ Cancelar
                  </button>
                </div>
              </div>
            ) : (
              // Modo de visualização
              <div className="inscrito-info">
                <div className="info-grid">
                  <div className="info-item">
                    <strong>Nome:</strong> {inscrito.nome_completo}
                  </div>
                  <div className="info-item">
                    <strong>CPF:</strong> {inscrito.documento}
                  </div>
                  <div className="info-item">
                    <strong>Responsável:</strong> {inscrito.responsavel}
                  </div>
                  <div className="info-item">
                    <strong>Líder de Célula:</strong> {inscrito.lider_celula}
                  </div>
                  <div className="info-item">
                    <strong>Telefone:</strong> {inscrito.tel_responsavel}
                  </div>
                  <div className="info-item">
                    <strong>Email:</strong> {inscrito.email}
                  </div>
                  <div className="info-item">
                    <strong>Campus:</strong> {inscrito.campus}
                  </div>
                  <div className="info-item">
                    <strong>Idade:</strong> {inscrito.idade}
                  </div>
                </div>

                {/* Link para visualizar PDF */}
                {inscrito.pdf_url && (
                  <div className="pdf-section">
                    <a 
                      href={`http://localhost:3001${inscrito.pdf_url}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="pdf-link"
                    >
                      📄 Visualizar Termo de Responsabilidade
                    </a>
                  </div>
                )}

                {/* Seção de assinatura */}
                {!inscrito.assinatura_realizada && (
                  <div className="signature-section">
                    <h3>Assinatura Digital</h3>
                    <p>Faça sua assinatura no campo abaixo:</p>

                    <div className="signature-container">
                      <SignatureCanvas
                        ref={sigCanvas}
                        canvasProps={{
                          width: 400,
                          height: 150,
                          className: 'signature-canvas'
                        }}
                      />
                    </div>

                    <div className="signature-buttons">
                      <button 
                        onClick={adicionarAssinatura}
                        disabled={loading}
                        className="sign-button"
                      >
                        {loading ? 'Processando...' : '✍️ Assinar Termo'}
                      </button>
                      <button 
                        onClick={limparAssinatura}
                        className="clear-button"
                      >
                        🗑️ Limpar
                      </button>
                    </div>
                  </div>
                )}

                {inscrito.assinatura_realizada && (
                  <div className="signed-status">
                    <p className="signed-message">✅ Termo assinado com sucesso!</p>
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
